import React from "react";
import { useState, useEffect, useCallback } from "react";
import Cart from "./Components/Cart/Cart";
import Header from "./Components/Layout/Header";
import Meals from "./Components/Meals/Meals";
import CartProvider from "./Store/CartProvider";


function App() {
  const [isClicked, setIsClicked] = useState(false);

  const [meals, setMeals] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const fetchMealsHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch("https://react-http-edb74-default-rtdb.firebaseio.com/meals.json");

      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const data = await response.json();
      console.log(data);


      const DUMMY_MEALS = [];

      for (const key in data) {
        DUMMY_MEALS.push({
          id: key,
          name: data[key].name,
          description: data[key].description,
          price: data[key].price
        });
      }
      setMeals(DUMMY_MEALS);
    } catch (error) {
      setError(error.message);
    }

    setIsLoading(false);
  }, []);

  let content = <p>Found no meals.</p>

  if(meals.length > 0) {
    content = <Meals mealsAvailable={meals} />
  }

  if(error) {
    content = <p>{error}</p>
  }

  if(isLoading) {
    content = <p>Loading...</p>
  }

  useEffect(() => {
    fetchMealsHandler();
  }, [fetchMealsHandler]);

  function handleClick() {
    setIsClicked(true);
  }

  function handleClose() {
    setIsClicked(false);
  }




  return (
    <CartProvider>
    {isClicked && <Cart onClose={handleClose} />}
      <Header onOpen={handleClick} />
      {content}
    </CartProvider>
  );
}

export default App;
