import Modal from "./Modal";
import "./Cart.css";
import { useContext, useState } from "react";
import CartContext from "../../Store/cart-context";
import CartItem from "./CartItem";
import OrderForm from "./OrderForm/OrderForm";

function Cart(props) {
    const [checkOut, setCheckOut] = useState(false);

    const cartCtx = useContext(CartContext);

    const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`;

    const hasItems = cartCtx.items.length > 0;

    function removeCartItemHandler(id) {
        cartCtx.removeItem(id);
    }

    function addCartItemHandler(item) {
        cartCtx.addItem({
            ...item,
            amount: 1
        });
    }

    function handleCheckOut() {
        setCheckOut(true);
    }

    const cartItems = <ul className="cart-items">{cartCtx.items.map((cartItem) => {
        return <CartItem
            key={cartItem.id}
            name={cartItem.name}
            price={cartItem.price}
            amount={cartItem.amount}
            onRemove={removeCartItemHandler.bind(null, cartItem.id)}
            onAdd={addCartItemHandler.bind(null, cartItem)}
        />;
    })}</ul>


    return (
        <Modal onHide={props.onClose}>
            {cartItems}
            <div className="total">
                <span>Total Amount</span>
                <span>{totalAmount}</span>
                { checkOut && <OrderForm />}
            </div>
          
            <div className="actions">
                <button className="button--alt" onClick={props.onClose}>Close</button>
                {hasItems && <button className="button" onClick={handleCheckOut}>Order</button>}
            </div>
        </Modal>
    );
}

export default Cart;
