import Modal from "./Modal";
import "./Cart.css";
import { useContext } from "react";
import CartContext from "../../Store/cart-context";

function Cart() {

    const closeCtx = useContext(CartContext);

    const cartItems = <ul className="cart-items">{[{id: "c1", name: "sushi", amount: "2", price: "$15"}].map((cartItem) => {
        return <li>{cartItem.name}</li>;
    })}</ul>


    return (
        <Modal>
            {cartItems}
            <div className="total">
                <span>Total Amount</span>
                <span>35.52</span>
            </div>
            <div className="actions">
                <button className="button--alt" onClick={closeCtx.onClose}>Close</button>
                <button className="button">Order</button>
            </div>
        </Modal>
    );
}

export default Cart;
