import { useState } from "react";
import classes from "./OrderForm.module.css";

const OrderForm = (props) => {

    const [name, setName] = useState("");
    const [nameIsTouched, setNameIsTouched] = useState(false);

    const nameIsValid = name.trim().length !== "";
    const nameIsInvalid = !nameIsValid && nameIsTouched;

    let formIsValid;

    if(nameIsValid) {
        formIsValid = true;
    }

    const [street, setStreet] = useState("");
    const [code, setCode] = useState("");
    const [city, setCity] = useState("");


    function nameChangeHandler(event) {
        setName(event.target.value);
    }

    function nameBlurHandler(event) {
        setNameIsTouched(true);
    }

    function streetChangeHandler(event) {
        setStreet(event.target.value);
    }

    function codeChangeHandler(event) {
        setCode(event.target.value);
    }

    function cityChangeHandler(event) {
        setCity(event.target.value);
    }

    const handleOrderForm = (event) => {
        event.preventDefault();

        setNameIsTouched(true);
        if(formIsValid) {
            console.log("submitted!");
            console.log(name);
        }
        
    }

    return (
        
            <form className={classes.form}>
                <div className={`${classes.control} ${nameIsInvalid && classes.invalid}`}>
                    <label htmlFor="name">Name</label>
                    <input id="name" type="text" value={name} onChange={nameChangeHandler} onBlur={nameBlurHandler} />
                    {nameIsInvalid && <p>Enter a valid name.</p>}
                </div>

                <div className={classes.control}>
                    <label htmlFor="address">Street</label>
                    <input id="address" type="text" value={street} onChange={streetChangeHandler} />
                </div>

                <div className={classes.control}>
                    <label htmlFor="postal-code">Postal Code</label>
                    <input id="postal-code" type="text" value={code} onChange={codeChangeHandler} />
                </div>

                <div className={classes.control}>
                    <label htmlFor="city">City</label>
                    <input id="city" type="text" value={city} onChange={cityChangeHandler} />
                </div>

                <div className={classes.actions}>
                    <button onSubmit={handleOrderForm}>Check-Out</button>
                    <button onClick={props.onCancel} disabled={!formIsValid}>Cancel</button>
                </div>
            </form>
        
    );
}

export default OrderForm;
